$id=$ARGV[0];$IP=$ARGV[1];$i=1;while($i<=3000){print "Iteration No: ".$i."\n\n";print("adb -s $id shell am start -d http://$IP:8080/17again.mp4 -t video/mp4 -a android.intent.action.VIEW");system("adb -s $id shell am start -d http://".$IP.":8080/17again.mp4 -t video/mp4 -a android.intent.action.VIEW");sleep 6400;system("adb -s $id shell input keyevent 4");system("adb -s $id shell input keyevent 4");$i++;}